// src/EventList.js
import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addEvent, removeEvent } from '../eventsSlice';
import { addToCalendar } from './calendarSlice';

function EventList() {
  const events = useSelector((state) => state.events.events);
  const dispatch = useDispatch();

  const [searchTerm, setSearchTerm] = useState('');

  const addEventToCalendar = (event) => {
    dispatch(addToCalendar(event));
  };

  const filteredEvents = events.filter((event) =>
    event.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="event-list">
      <h2>Event List</h2>
      <input
        type="text"
        placeholder="Search events..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <ul>
        {filteredEvents.map((event) => (
          <li key={event.id}>
            {event.name}
            <button onClick={() => addEventToCalendar(event)}>Add to Calendar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default EventList;
