// src/calendarSlice.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  calendar: [],
};

const calendarSlice = createSlice({
  name: 'calendar',
  initialState,
  reducers: {
    addToCalendar: (state, action) => {
      state.calendar.push(action.payload);
    },
    removeFromCalendar: (state, action) => {
      state.calendar = state.calendar.filter((event) => event.id !== action.payload);
    },
  },
});

export const { addToCalendar, removeFromCalendar } = calendarSlice.actions;
export default calendarSlice.reducer;
