// src/EventCalendar.js
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addToCalendar, removeFromCalendar } from './calendarSlice';

function EventCalendar() {
  const events = useSelector((state) => state.events.events);
  const calendar = useSelector((state) => state.calendar.calendar);
  const dispatch = useDispatch();
  const [selectedEvent, setSelectedEvent] = React.useState('');

  const addEventToCalendar = () => {
    if (selectedEvent) {
      dispatch(addToCalendar(selectedEvent));
      setSelectedEvent('');
    }
  };

  const removeEventFromCalendar = (eventId) => {
    dispatch(removeFromCalendar(eventId));
  };

  return (
    <div className="event-calendar">
      <h2>Event Calendar</h2>
      <select value={selectedEvent} onChange={(e) => setSelectedEvent(e.target.value)}>
        <option value="">Select an event to add</option>
        {events.map((event) => (
          <option key={event.id} value={event.id}>
            {event.name}
          </option>
        ))}
      </select>
      <button onClick={addEventToCalendar}>Add to Calendar</button>
      <ul>
        {calendar.map((event) => (
          <li key={event.id}>
            {event.name}
            <button onClick={() => removeEventFromCalendar(event.id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default EventCalendar;
